const nameList = [
  { nameId: 1, name: "Mike" },
  { nameId: 2, name: "Bob" },
  { nameId: 3, name: "Bob" },
  { nameId: 4, name: "Bob" },
  { nameId: 5, name: "Bob" },
  { nameId: 6, name: "Bob" },
  { nameId: 7, name: "Bob" },
  { nameId: 8, name: "Bob" },
];

const Profile = () => {
  nameList.map((person) => {
    return <p>{person.name}</p>;
  });
};

const List = () => {
  return (
    <div>
      <style jsx>{`
        .h1 {
          display: flex;
          flex-direction: row;
          flex: 1;
        }

        nav {
          height: 200px;
          width: 18%;
          position: absolute;
          top: 20px;
          right: 20px;
          overflow: scroll;
          margin: 0;
          padding: 0;

          border: 1px solid #ccc;

          font-size: 16px;
          font-family: Arial, sans-serif;

          // Again, this is where the magic happens
          -webkit-overflow-scrolling: touch;
        }
        ul {
          overflow: hidden;
          overflow-y: scroll;
          list-style-type: none;
        }

        li {
          padding: 10px 20px;

          border-bottom: 1px solid #ccc;
        }
        li:nth-child(even) {
          background: #f8f8f8;
        }
        li:last-child {
          border-bottom: none;
        }
      `}</style>

      <nav>
        <ul>
          {nameList.map((person) => {
            return <li>{person.name}</li>;
          })}
        </ul>
      </nav>
    </div>
  );
};

export default List;
