// import { useDispatch } from "react-redux";
// import useInterval from "../lib/useInterval";
import React, { Component } from "react";
import Clock from "./clock";
// import Counter from './counter'
import Nav from "./nav";
import moment from "moment";

export default class Page extends Component {
  constructor(props) {
    super(props);

    this.state = {
      input: "",
      remainTime: 0,
      endDate: moment(),
    };

    let interval;
  }

  handleCountDown = (e) => {
    const inputTime = parseInt(e.target.value);

    if (Number.isInteger(inputTime)) {
      if (inputTime <= 99 && inputTime >= 1) {
        console.log("inputTime", inputTime);
        this.setState({
          input: inputTime,
        });
      }
    } else {
      alert("Should enter Integer");
    }
  };

  setMin = () => {
    this.setState({
      remainTime: this.state.input * 60,
      input: "",
    });
    this.countDown();
  };

  setSec = () => {
    this.setState({
      remainTime: this.state.input,
      input: "",
    });
    this.countDown();
  };

  countDown = () => {
    clearInterval(this.interval);
    this.interval = setInterval(() => {
      this.setState({
        remainTime: (this.state.remainTime -= 1),
      });
      if (this.state.remainTime <= 0) {
        clearInterval(this.interval);
      }
    }, 1000);
  };
  // const dispatch = useDispatch();

  // // Tick the time every second
  // useInterval(() => {
  //   dispatch({
  //     type: "TICK",
  //     light: true,
  //     lastUpdate: Date.now(),
  //   });
  // }, 1000);
  render() {
    return (
      <>
        <Nav />

        <Clock remainTime={this.state.remainTime} />

        <div className={"main-layout"}>
          <style jsx>{`
            .main-layout {
              display: flex;
              justify-content: space-between;
              padding-top: 1rem;
              padding-bottom: 1rem;
            }

            .custom-button {
              padding: 10px;
              display: inline-block;
              color: black;
              font: 14px menlo, monaco, monospace;
              background-color: white;
            }
            .custom-button:hover {
              background-color: red;
              cursor: pointer;
            }
          `}</style>
          <input
            type="text"
            value={this.state.input}
            onChange={this.handleCountDown}
          ></input>
          <button
            className={"custom-button"}
            type="submit"
            onClick={this.setMin}
          >
            CountDown
          </button>
          <button type="submit" onClick={this.setSec}>
            Debug Mode
          </button>
        </div>
      </>
    );
  }
}
