const nameList = [
  { nameId: 1, name: "Mike" },
  { nameId: 2, name: "Bob" },
  { nameId: 3, name: "Bob" },
  { nameId: 4, name: "Bob" },
  { nameId: 5, name: "Bob" },
  { nameId: 6, name: "Bob" },
  { nameId: 7, name: "Bob" },
  { nameId: 8, name: "Bob" },
  { nameId: 9, name: "Jane" },
];

const Profile = () => {
  nameList.map((person) => {
    return <p>{person.name}</p>;
  });
};

const List = () => {
  return (
    <div>
      <style jsx>{`
        nav {
          height: 300px;
          width: 200px;
          overflow: scroll;
          border: 1px solid #ccc;

          font-size: 14px;
          font-family: Arial, sans-serif;

          -webkit-overflow-scrolling: touch;
        }
        ul {
          overflow: hidden;
          overflow-y: scroll;
          list-style-type: none;
          padding-inline-start: 0;
          margin-block-start: 0;
          margin-block-end: 0;
        }

        li {
          padding: 0.8rem 1.2rem;
          border-bottom: 1px solid #ccc;
        }
        li:nth-child(even) {
          background: #f8f8f8;
        }
        li:last-child {
          border-bottom: none;
        }
      `}</style>

      <nav>
        <ul>
          {nameList.map((person) => {
            return <li key={person.nameId}>{person.name}</li>;
          })}
        </ul>
      </nav>
    </div>
  );
};

export default List;
