import Avatar from "react-avatar";

const Profile = () => {
  nameList.map((person) => {
    return <p>{person.name}</p>;
  });
};

const List = (props) => {
  const { nameList } = props;
  return (
    <div>
      <style jsx>{`
        nav {
          height: 300px;
          width: 200px;
          overflow: scroll;
          border: 1px solid #ccc;

          font-size: 14px;
          font-family: Arial, sans-serif;

          -webkit-overflow-scrolling: touch;
        }

        .profile {
        }

        ul {
          overflow: hidden;
          overflow-y: scroll;
          list-style-type: none;
          padding-inline-start: 0;
          margin-block-start: 0;
          margin-block-end: 0;
          display: flex;
          justify-content: space-around;
        }

        li {
          padding: 0.8rem 1.2rem;
          border-bottom: 1px solid #ccc;
        }
        li:nth-child(even) {
          background: #f8f8f8;
        }
        li:last-child {
          border-bottom: none;
        }
      `}</style>

      <nav>
        <ul>
          {nameList.map((person, i) => {
            return (
              <div key={i} className={"profile"}>
                <div>
                  <li key={person.nameId}>{person.name}</li>
                </div>
                <div>
                  <Avatar
                    size={"30"}
                    round={true}
                    key={`${person.nameId + 10}`}
                    alt={person.name}
                    src={`https://robohash.org/${person.name}?set=set4&size=35x35`}
                  />
                </div>
              </div>
            );
          })}
        </ul>
      </nav>
    </div>
  );
};

export default List;
