import Link from "next/link";

const ResultView = ({ resultName }) => {
  return (
    <>
      <style jsx>{`
        .container {
          width: 300px;
          margin: 100px auto;
        }
        p {
          display: flex;
          justify-content: center;
          font: 20px menlo, monaco, monospace;
        }
        .link {
          display: flex;
          justify-content: center;
        }
        .link-button {
          font: bold 11px Arial;
          text-decoration: none;
          background-color: #eeeeee;
          color: #333333;
          padding: 2px 6px 2px 6px;
          border-top: 1px solid #cccccc;
          border-right: 1px solid #333333;
          border-bottom: 1px solid #333333;
          border-left: 1px solid #cccccc;
        }
      `}</style>
      <div className={"container"}>
        <p>抽獎結果</p>
        <img
          top
          src={`https://robohash.org/${resultName}?set=set4&size=300x300`}
          alt="monster"
        />
        <p>{resultName}</p>
        <div className={"link"}>
          <Link href="/">
            <a className={"link-button"}>Try Again</a>
          </Link>
        </div>
      </div>
    </>
  );
};

const Result = (props) => {
  const { resultName, isCountDown } = props;

  return (
    <>
      {isCountDown ? (
        <ResultView resultName={resultName} />
      ) : (
        <Link href="/">
          <a>Try Again</a>
        </Link>
      )}
    </>
  );
};
export default Result;
