import Link from "next/link";

const ResultView = ({ resultName }) => {
  return (
    <>
      <style jsx>{`
        .container {
          width: 300px;
          margin: 100px auto;
        }
        p {
          display: flex;
          justify-content: center;
          font: 20px menlo, monaco, monospace;
        }
        .link {
          display: flex;
          justify-content: center;
        }
        .link-button {
          font: 14px menlo, monaco, monospace;
          text-decoration: none;
          color: #333333;
          padding: 10px;
          border: 1px solid #333333;
        }
      `}</style>
      <div className={"container"}>
        <p>抽獎結果</p>
        <img
          top
          src={`https://robohash.org/${resultName}?set=set4&size=300x300`}
          alt="monster"
        />
        <p>{resultName}</p>
        <div className={"link"}>
          <Link href="/">
            <a className={"link-button"}>Try Again</a>
          </Link>
        </div>
      </div>
    </>
  );
};

const Result = (props) => {
  const { resultName, isCountDown } = props;

  return (
    <>
      {isCountDown ? (
        <ResultView resultName={resultName} />
      ) : (
        <div className={"link"}>
          <Link href="/">
            <a>Try Again</a>
          </Link>
        </div>
      )}
    </>
  );
};
export default Result;
