import { useSelector, shallowEqual } from "react-redux";
import React, { Component } from "react";
import moment from "moment";

const useClock = () => {
  return useSelector(
    (state) => ({
      lastUpdate: state.lastUpdate,
      light: state.light,
    }),
    shallowEqual
  );
};

