import { useState, useSelector, shallowEqual } from "react-redux";
import React, { Component } from "react";
import moment from "moment";

const useClock = () => {
  return useSelector(
    (state) => ({
      lastUpdate: state.lastUpdate,
      light: state.light,
    }),
    shallowEqual
  );
};

// const formatTime = (time) => {
//   // cut off except hh:mm:ss
//   // return new Date(time).toJSON().slice(11, 19)
//   const timer = moment().startOf("day").add(this.state.remindTime, "second");
//   if (this.state.remindTime >= 3600) {
//     return timer.format("HH:mm:ss");
//   }
//   return timer.format("mm:ss");
// };

export default class Clock extends Component {
  constructor(props) {
    super(props);

    this.state = {
      remainTime: 60,
      endDate: moment(),
    };

    let interval;
  }

  formatTime = () => {
    // cut off except hh:mm:ss
    // return new Date(time).toJSON().slice(11, 19)
    const timer = moment().startOf("day").add(this.state.remainTime, "second");
    if (this.state.remainTime >= 3600) {
      return timer.format("HH:mm:ss");
    }
    return timer.format("mm:ss");
  };

  countDown = (second) => {
    this.setState({
      remainTime: second,
      endDate: moment().add(second, "second"),
    });
    clearInterval(this.interval);
    this.interval = setInterval(() => {
      setRemainTime({
        remainTime: (this.state.remainTime -= 1),
      });
      if (this.state.remainTime <= 0) {
        clearInterval(this.interval);
      }
    }, 1000);
  };

  // const { lastUpdate, light } = useClock();
  render() {
    return (
      <div className={""}>
        {this.formatTime()}
        <style jsx>{`
          div {
            padding: 15px;
            display: inline-block;
            color: #82fa58;
            font: 50px menlo, monaco, monospace;
            background-color: #000;
          }

          .light {
            background-color: #999;
          }
        `}</style>
      </div>
    );
  }
}
