// import { useDispatch } from "react-redux";
// import useInterval from "../lib/useInterval";
import React, { Component } from "react";
import Clock from "./clock";
// import Counter from './counter'
import Nav from "./nav";
import List from "./list";

import Link from "next/link";
import moment from "moment";
import Router from "next/router";

export default class Page extends Component {
  constructor(props) {
    super(props);

    this.state = {
      input: "",
      remainTime: 0,
      endDate: moment(),
      result: "",
    };

    this.interval;
  }

  componentDidMount() {
    const { resetAllStatus } = this.props;
    resetAllStatus();
  }

  handleCountDown = (e) => {
    const inputTime = parseInt(e.target.value);
    this.setState({
      input: inputTime,
    });
    if (Number.isInteger(inputTime) || "") {
      if (inputTime <= 59 && inputTime >= 1) {
        console.log("inputTime", inputTime);
      }
    } else {
    }
  };

  inputCheck = () => {
    if (this.state.input <= 59 && this.state.input >= 1) {
      return true;
    } else {
      return false;
    }
  };

  setMin = () => {
    if (this.inputCheck()) {
      console.log("inputTime", inputTime);
      this.setState({
        remainTime: this.state.input * 60,
        input: "",
      });
      this.countDown();
    } else {
      this.errorMessege();
    }
  };

  setSec = () => {
    if (this.inputCheck()) {
      this.setState({
        remainTime: this.state.input,
        input: "",
      });
      this.countDown();
    } else {
      this.errorMessege();
    }
  };

  errorMessege = () => {
    alert("輸入1-59的數字");
  };

  countDown = () => {
    clearInterval(this.interval);
    this.interval = setInterval(() => {
      this.setState({
        remainTime: (this.state.remainTime -= 1),
      });
      if (this.state.remainTime <= 0) {
        clearInterval(this.interval);
        console.log("selected", this.randomSelect().name);
        this.setState({
          result: this.randomSelect().name,
        });
        this.openResultPage();
      }
    }, 1000);
  };

  openResultPage = () => {
    console.log("props", this.props);
    const resultName = this.randomSelect().name;
    this.props.setResultName(resultName);
    this.props.changeCountDownState();
    Router.push("/result_page");
    console.log("props", this.props);
  };

  randomSelect = () => {
    const { nameList } = this.props;
    return nameList[Math.floor(Math.random() * nameList.length)];
  };

  // const dispatch = useDispatch();

  // // Tick the time every second
  // useInterval(() => {
  //   dispatch({
  //     type: "TICK",
  //     light: true,
  //     lastUpdate: Date.now(),
  //   });
  // }, 1000);
  render() {
    const { nameList } = this.props;
    return (
      <>
        <div className={"container"}>
          <style jsx>{`
            .container {
              width: 50%;
              margin: 5rem auto;
              display: flex;
              justify-content: space-between;

              flex-direction: row;
            }

            @media (max-width: 768px) {
              .container {
                flex-direction: column;
                justify-content: center;
              }
              .right-area {
                margin-top: 5rem;
              }
              .left-area {
                width: 100%;
                margin: 10px;
              }

              .top-area {
                width: 200px;
                flex-direction: column;
              }
              .custom-button {
                margin-left: 0;
              }
            }

            .left-area {
              width: 50%;
            }

            .custom-button {
              border: 1px solid #333333;
              margin-left: 0.3rem;
              padding: 10px;
              display: inline-block;
              color: black;
              font: 14px menlo, monaco, monospace;
              background-color: white;
            }
            .custom-button:hover {
              background-color: #dbdbdb;
              cursor: pointer;
            }

            .right-area {
              display: flex;
              justify-content: center;
            }

            .top-area {
              display: flex;
            }
          `}</style>
          <div className={"left-area"}>
            <div className={"top-area"}>
              <input
                type="number"
                value={this.state.input}
                onChange={this.handleCountDown}
                pattern="[0-9]*"
              ></input>
              <button
                className={"custom-button"}
                type="submit"
                onClick={this.setMin}
              >
                CountDown
              </button>
              <button
                className={"custom-button"}
                type="submit"
                onClick={this.setSec}
              >
                Debug Mode
              </button>
            </div>

            <Clock remainTime={this.state.remainTime} />
          </div>
          <div className={"right-area"}>
            <List nameList={nameList} />
          </div>
        </div>
      </>
    );
  }
}
