const nameList = [
  { nameId: 1, name: "Mike" },
  { nameId: 1, name: "Bob" },
  { nameId: 1, name: "Bob" },
  { nameId: 1, name: "Bob" },

  { nameId: 1, name: "Bob" },

  { nameId: 1, name: "Bob" },

