  { nameId: 1, name: "Bob" },
];

const Profile = () => {
  nameList.map((person) => {
    return <p>{person.name}</p>;
  });
};

const List = () => {
  return (
    <div>
      <style jsx>{`
        .h1 {
          display: flex;
          flex-direction: row;
          flex: 1;
        }

        nav {
          height: 200px;
          width: 18%;
          position: absolute;
          top: 20px;
          right: 20px;
          overflow: scroll;
          margin: 0;
          padding: 0;

          border: 2px solid #ccc;

          font-size: 16px;
          font-family: Arial, sans-serif;

          // Again, this is where the magic happens
          -webkit-overflow-scrolling: touch;
        }
        ul {
          overflow: hidden;
          overflow-y: scroll;
        }
      `}</style>

      <nav>
        <ul>
          <li>
            {nameList.map((person) => {
              return <p>{person.name}</p>;
            })}
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default List;
