// import { useDispatch } from "react-redux";
// import useInterval from "../lib/useInterval";
import React, { Component } from "react";
import Clock from "./clock";
// import Counter from './counter'
import Nav from "./nav";
import List from "./list";
import moment from "moment";

export default class Page extends Component {
  constructor(props) {
    super(props);

    this.state = {
      input: "",
      remainTime: 0,
      endDate: moment(),
    };

    let interval;
  }

  handleCountDown = (e) => {
    const inputTime = parseInt(e.target.value);

    if (Number.isInteger(inputTime)) {
      if (inputTime <= 99 && inputTime >= 1) {
        console.log("inputTime", inputTime);
        this.setState({
          input: inputTime,
        });
      }
    } else {
      alert("Should enter Integer");
    }
  };

  setMin = () => {
    this.setState({
      remainTime: this.state.input * 60,
      input: "",
    });
    this.countDown();
  };

  setSec = () => {
    this.setState({
      remainTime: this.state.input,
      input: "",
    });
    this.countDown();
  };

  countDown = () => {
    clearInterval(this.interval);
    this.interval = setInterval(() => {
      this.setState({
        remainTime: (this.state.remainTime -= 1),
      });
      if (this.state.remainTime <= 0) {
        clearInterval(this.interval);
      }
    }, 1000);
  };
  // const dispatch = useDispatch();

  // // Tick the time every second
  // useInterval(() => {
  //   dispatch({
  //     type: "TICK",
  //     light: true,
  //     lastUpdate: Date.now(),
  //   });
  // }, 1000);
  render() {
    return (
      <>
        <div className={"container"}>
          <style jsx>{`
            .container {
              width: 60%;
              margin: 5rem auto;
              display: flex;
              justify-content: space-between;

              flex-direction: row;
            }

            @media (max-width: 768px) {
              .container {
                flex-direction: column;
              }
              .right-area {
                margin-top: 5rem;
              }
            }

            .left-area {
              width: 50%;
            }

            .custom-button {
              margin-left: 0.3rem;
              padding: 10px;
              display: inline-block;
              color: black;
              font: 14px menlo, monaco, monospace;
              background-color: white;
            }
            .custom-button:hover {
              background-color: red;
              cursor: pointer;
            }

            .right-area {
              display: flex;
              justify-content: center;
            }
          `}</style>
          <div className={"left-area"}>
            <input
              type="text"
              value={this.state.input}
              onChange={this.handleCountDown}
            ></input>
            <button
              className={"custom-button"}
              type="submit"
              onClick={this.setMin}
            >
              CountDown
            </button>
            <button
              className={"custom-button"}
              type="submit"
              onClick={this.setSec}
            >
              Debug Mode
            </button>
            <Clock remainTime={this.state.remainTime} />
          </div>
          <div className={"right-area"}>
            <List />
          </div>
        </div>
      </>
    );
  }
}
